import React, { useState, useEffect } from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, Form, InputNumber, Divider, Button, Modal, Drawer } from 'antd'
import { TcrbButton, TcrbPopconfirm } from '../../antd-styles/styles'
import { inject, observer } from 'mobx-react'
import { orange, green, gold } from '@ant-design/colors';

const ManageDepartment = () => {


  const onEditSection = () => {
    setAddSection(true)
    setModalType('')
    setmodalString(
      <div>
        <SelectDepartment />
        <Row justify="end" style={{ marginTop: 8 }}>
          <Col span={4} style={{ textAlign: "right" }}>
            <TcrbPopconfirm title="Confirm to Add Section/Team to Department" onConfirm={() => { /*waiting for api */ }} okText="Confirm" cancelText="No">
              <Button className="primary" >Add</Button>
            </TcrbPopconfirm>
          </Col>
        </Row>
        <Divider />
        <ListSection />
      </div>
    )
  }


  const renderActionSectionAndTeam = (record) => {
    const editable = isEditingSection(record);
    if (record.status === '1') {
      return editable ? (
        <span>
          <TcrbPopconfirm title="Sure to Save?" onConfirm={() => save(record.key)}>
            <a style={{ marginRight: 8, }}>
              Save
          </a>
          </TcrbPopconfirm>
          <TcrbPopconfirm title="Sure to cancel?" onConfirm={() => setEditingKeySection('')}>
            <a style={{ color: '#3e3e3e' }}>Cancel</a>
          </TcrbPopconfirm>
        </span>
      ) : (
          <div style={{ textAlign: "center" }}>
            {/* <TcrbPopconfirm title="Sure to Edit?" onConfirm={() => edit(record)}> */}
            <a disabled={editingKeySection !== ''} onClick={() => editSection(record)} style={{ marginRight: 8, color: '#FBA928' }}>
              Edit
          </a>
            {/* </TcrbPopconfirm> */}
            <TcrbPopconfirm title="Sure to Deactivate?" >
              <a style={{ color: '#3e3e3e' }}>Deactivate</a>
            </TcrbPopconfirm>
            {/* <TcrbPopconfirm title="Sure to Delete?" disabled={editingKey !== ''} onConfirm={() => deletePartnerSelect(record)}>
              <a><DeleteOutlined style={{ fontSize: '18px', paddingRight: 8 }} /></a>
            </TcrbPopconfirm>
            <a disabled={editingKey !== ''} onClick={() => edit(record)}><EditOutlined style={{ fontSize: '18px', color: '#FBA928' }} /></a> */}
          </div>
        );
    } else if (record.status === '2') {
      return null
    } else {
      return null
    }
  }
  return (
    <div>

    </div>
  )
}

export default ManageDepartment
