import React, { useState, useEffect, useContext } from 'react'
import { Input, Row, Col, Layout, Modal, Switch } from 'antd'
import styled from 'styled-components';
import SimpleSearch from '../simple-search'
import SimpleModal from '../simple-modal'
import { observer, inject } from 'mobx-react';
import { useStores } from '../../hooks/use-stores'
const { Header, Footer, Sider, Content } = Layout;
const stringSwitch = [{ accountNumber: '123123123123', accountType: 'Binding to TCRB Mobile Banking', accountStatus: true }, { accountNumber: '00993445123123', accountType: 'Binding to Micro Pay', accountStatus: false }]


const StyledSpan = styled.span`

  display: initial;
  padding-left: ${({ theme }) => theme.spacing.medium}px !important;
`
const StyledSwitch = styled(Switch)`
  ${'' /* .ant-switch-checked  {
        background-color: red !important;
      }
      .ant-switch{
        background-color: red !important;
      } */}
      button .ant-switch{
        background-color: red !important;
      }
`

export default function OtpUnlocking
  () {
  const [idCard, setIdCard] = useState('');
  const [isSearch, setIsSearch] = useState(false);
  const [visible, setVisble] = useState(false)
  const [isChecked, setIsChecked] = useState(false)
  const [modalString, setModalString] = useState('')
  const { customerServicesMenuStore } = useStores()
  const searchIdCardNumber = (value) => {
    console.log('eiei search:' + value)
    setIdCard(value)
    setIsSearch(true)
  }


  const hideModal = () => {
    this.setState({
      visible: false,
    });
  };
  const onChange = (value, switchSelected) => {
    console.log("change:" + value)
    console.log(JSON.stringify(switchSelected))
    if (switchSelected.accountStatus === true) {
      setVisble(true)
      setIsChecked(true)
      setModalString(
        <div style={{ textAlign: "center" }}>
          <p>Unlocking OTP!!</p>
          <p>Account Number {switchSelected.accountNumber}</p>
        </div>
      )
    } else {
      // setIsChecked(false)
    }
  }
  const unlockOTP = () => {
    //some action
    setVisble(false)
  }
  const SwitchList = () => {
    const listItems = stringSwitch.map((string, index) =>
      <Row key={index} gutter={[4, 8]}>
        <Col span={5}>
          <Switch defaultChecked={string.accountStatus} onChange={checked => onChange(checked, string)} disabled={string.accountStatus === false} />
          <StyledSpan>{string.accountNumber}</StyledSpan>
        </Col>
        <Col span={6}>
          <StyledSpan> {string.accountType}</StyledSpan>
        </Col>
        <Col span={4}>
          {string.accountStatus ? (<StyledSpan>OTP is Locked</StyledSpan>) : (<StyledSpan>OTP is ready for using</StyledSpan>)}
        </Col>
      </Row>
    );
    return (
      <ul>{listItems}</ul>
    );
  }
  return (
    <div style={{ marginTop: 20 }}>
      <Row>
        <Switch>EiEI</Switch>
      </Row>
      <Row gutter={[4, 24]}>
        <SimpleSearch search={searchIdCardNumber} prefixWording="ID Card Number" />
      </Row>
      {(isSearch) ? (

        <Col flex={100}>
          <SwitchList />
        </Col>
      ) : ('')}
      <SimpleModal
        onOk={() => unlockOTP()}
        onCancel={() => setVisble(false)}
        okText="Confirm"
        cancelText="Cancel"
        modalString={modalString}
        visible={visible}
      />
    </div>
  )
}
