import React, { useEffect, useState } from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, Form, InputNumber, Divider, Button, Modal, Drawer, Space } from 'antd'
import { inject, observer } from 'mobx-react'
import { TcrbButton, TcrbPopconfirm } from '../../../antd-styles/styles'


import SimpleModal from '../../../simple-modal'
import SimpleInput from '../../../simple-input'
const ManageMenu =
  inject('userAccessManagementStore')
    (observer((props) => {
      const [editingKey, setEditingKey] = useState('')
      const [title, setTitle] = useState("")
      const [textOk, setTextOk] = useState("Submit")
      const [textEdit, setTextEdit] = useState("Add Section")
      const [textCancel, setTextCancel] = useState("Cancel")
      const [modalString, setModalString] = useState("")
      const [modalType, setModalType] = useState('')
      const [visible, setVisible] = useState(false)
      const [isDisableButtonAddFunction, setIsDisableButtonAddFunction] = useState(false)
      const [showEditMenu, setShowEditMenu] = useState(false)

      const [form] = Form.useForm();

      const { userAccessManagementStore, t } = props
      const isEditing = record => record.key === editingKey

      const edit = record => {
        form.setFieldsValue({
          name: '',
          status: record.status,
          ...record,
        });
        setEditingKey(record.key);
      }

      const EditableCell = ({
        editing,
        dataIndex,
        title,
        inputType,
        record,
        index,
        children,
        ...restProps
      }) => {
        return (
          <td {...restProps}>
            {editing ? (
              <Form.Item
                name={dataIndex}
                style={{
                  margin: 0
                }}
                rules={[
                  {
                    required: true,
                    message: `Please Input ${title}!`,
                  },
                ]}
              >
                <Input />
              </Form.Item>
            ) : (
                children
              )
            }
          </td>
        )
      }
      const goBackToMenuList = () => {
        userAccessManagementStore.nextPageIsManageMenu = false

      }

      const addNewFunction = () => {
        let newFunction = {
          key: dataSourceSection.length + 1,
          name: ''
        }
        setDataSourceSection([...dataSourceSection, newFunction])
        edit(newSection)
        setIsDisableButtonAddSection(true)
      }

      const cancelEditMenu = () => {
        setShowEditMenu(false)
      }

      const renderActionFunction = (record) => {
        const editable = isEditing(record);
        if (record.request_status === 'APPROVE') {
          return editable ? (
            <span>
              <TcrbPopconfirm title="Sure to Save?" onConfirm={() => submitEditFunction(record.key)}>
                <a style={{ marginRight: 8, }}>
                  Save
                  </a>
              </TcrbPopconfirm>
              <TcrbPopconfirm title="Sure to cancel?" onConfirm={() => cancelEditFunction()}>
                <a style={{ color: '#3e3e3e' }}>Cancel</a>
              </TcrbPopconfirm>
            </span>
          ) : (
              <div style={{ textAlign: "center" }}>
                <a disabled={editingKey !== ''} onClick={() => edit(record)} style={{ marginRight: 8, color: '#FBA928' }}>
                  Edit
                  </a>
                <TcrbPopconfirm title="Sure to Deactivate?" onConfirm={() => deactivateFunctionSelect(record)}>
                  <a style={{ color: '#FBA928' }}>Deactivate</a>
                </TcrbPopconfirm>
              </div>
            );
        } else if (record.request_status === 'PENDING') {
          return null
        } else if (record.request_status === 'REJECT') {
          return null
        }
        else {
          return null
        }
      }

      const columnFunction = [
        {
          title: '',
          dataIndex: 'status',
          width: '5%',
          render: (text, record) => checkDefaultStatus(record.status, record.request_status)
        },
        {
          title: 'Function Name',
          dataIndex: 'name',
          editable: true,
          // render: (text, record) => (record.partner_code + "/" + record.partner_abbreviation)
        },
        {
          title: 'Action',
          dataIndex: 'operation',
          width: '10%',
          render: (text, record) => renderActionFunction(record)
        }
      ]

      const mergedColumns = columnFunction.map(col => {
        if (!col.editable) {
          return col;
        }

        return {
          ...col,
          onCell: record => ({
            record,
            inputType: 'text',
            dataIndex: col.dataIndex,
            title: col.title,
            editing: isEditing(record),
          }),
        };
      });
      return (
        <div>
          <Row gutter={[4, 24]}>
            <Col span={2}>
              <TcrbButton className="default" onClick={() => goBackToMenuList()}>Back</TcrbButton>
            </Col>
          </Row>
          <Row gutter={[4, 24]}>
            <Col span={5}>Menu</Col>
            <Col span={7}> {showEditMenu ?
              <SimpleInput defaultValue={userAccessManagementStore.menuSelected.name} onChange={(value) => departmentName = value} /> : userAccessManagementStore.menuSelected.name
            }
            </Col>
          </Row>

          <Row justify="center" style={{ marginTop: 8 }}>
            <Col span={4}>
              {showEditMenu ?
                <Space size={8}>
                  <TcrbButton className="default" onClick={() => { cancelEditMenu() }} >Cancel</TcrbButton>
                  <TcrbButton className="primary" onClick={() => { openModalSubmitEditDepartmentName() }} >Submit</TcrbButton>
                </Space>
                :
                <TcrbButton className="primary" onClick={() => { setShowEditMenu(true) }} >Edit</TcrbButton>}
            </Col>
          </Row>
          <Divider />
          <Row gutter={[4, 24]}>
            <Col span={2}>
              <TcrbButton className="primary" onClick={() => addNewFunction()} disabled={isDisableButtonAddFunction}>Add Function</TcrbButton>
            </Col>
          </Row>
          <Row>
            <Col flex={100}>
              <Form form={form} component={false}>
                <Table
                  components={{
                    body: {
                      cell: EditableCell,
                    },
                  }}
                  bordered
                  dataSource={[]}
                  columns={mergedColumns}
                  size="small"
                />
              </Form>
            </Col>
          </Row>
          <SimpleModal
            title={title}
            type={modalType}
            onOk={() => submitEditDepartmentName()}
            onCancel={() => setVisible(false)}
            textCancel={textCancel}
            textOk={textOk}
            width={600}
            modalString={modalString}
            visible={visible}
          />

        </div>
      )
    }))

export default ManageMenu
