import React, { useState, useEffect } from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, InputNumber, Divider, Button, Modal, Drawer, Form, Radio } from 'antd'
import { TcrbButton, TcrbPopconfirm } from '../../antd-styles/styles'

import SimpleInput from '../../simple-input'


const UserList = () => {
  const [visible, setVisible] = useState(false)
  const [form] = Form.useForm();

  const SignupForm = () => {
    // Pass the useFormik() hook initial form values and a submit function that will
    // be called when the form is submitted
    const formik = useFormik({
      initialValues: {
        email: '',
      },
      onSubmit: values => {
        alert(JSON.stringify(values, null, 2));
      },
    });
    return (
      <form onSubmit={formik.handleSubmit}>
        <label htmlFor="email">Email Address</label>
        <Input
          id="email"
          name="email"
          type="email"
          onChange={formik.handleChange}
          value={formik.values.email}
        />
        <Button type="submit">Submit</Button>
      </form>
    );
  };

  const CollectionCreateForm = ({ visible, onCreate, onCancel }) => {
    return (
      <Modal
        visible={visible}
        title="Create a new collection"
        okText="Create"
        cancelText="Cancel"
        onCancel={onCancel}
        onOk={() => {
          form
            .validateFields()
            .then(values => {
              form.resetFields();
              onCreate(values);
            })
            .catch(info => {
              console.log('Validate Failed:', info);
            });
        }}
      >
        <Form
          form={form}
          layout="vertical"
          name="form_in_modal"
          initialValues={{
            modifier: 'public',
          }}
        >
          <Row>
            <Col span={10} style={{ padding: 4 }}>
              <p>
                Department Name
            </p>
            </Col>
            <Col span={14}>
              <Form.Item
                name="title"

                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <SimpleInput />
              </Form.Item>
            </Col>
          </Row>
          <Row>
            <Col>

            </Col>
            <Col>
              <Form.Item name="description" label="Description">
                <Input type="textarea" />
              </Form.Item>
            </Col>
          </Row>



        </Form>
      </Modal>
    );
  }

  const onCreate = values => {
    console.log('Received values of form: ', values);
    setVisible(false);
  }

  return (
    <div>
      <Row gutter={[4, 24]}>
        <Col span={2}>
          <TcrbButton className="primary" onClick={() => setVisible(true)} >Add User</TcrbButton>
        </Col>
      </Row>
      {/* <SignupForm /> */}
      <CollectionCreateForm
        visible={visible}
        onCreate={onCreate}
        onCancel={() => {
          setVisible(false);
        }}
      />
      {/* <Formik
        initialValues={{ name: "", email: "" }}
        onSubmit={async values => {
          await new Promise(resolve => setTimeout(resolve, 500));
          alert(JSON.stringify(values, null, 2));
        }}
      >
        <Form>
          <Field name="name" type="text" />
          <Field name="email" type="email" />
          <button type="submit">Submit</button>
        </Form>
      </Formik> */}
    </div>
  )
}

export default UserList
