import React, { useState, useEffect } from 'react'
import styled from 'styled-components';
import { Select } from 'antd';
const { Option } = Select;


export default function SimpleMenu(props) {
  return (
    <div flex={100}>
      <Select onChange={(event) => console.log(event.currentTarget.value)}>
        {props.options.map((item, index) => <Option key={index} value={item.value}>{item.display}</Option>)}
      </Select>
    </div>
  )

}
