import React, { useState, useEffect } from 'react'
import { Button, Table, Popconfirm, Row, Col } from 'antd'
// import { useStores } from '../../hooks/use-stores'
import { inject, observer } from 'mobx-react'
import { withTranslation } from '../../i18n'

const ProductLimitSetup =
  inject('businessParametersSetupStore')
    (observer((props) => {
      const [dataSource, setDataSource] = useState([])
      const { businessParametersSetupStore } = props

      useEffect(() => {
        // setDataSource(mockDataSource)
        businessParametersSetupStore.getDataProductLimit()

      }, []);
      useEffect(() => {
        console.log(toJS(businessParametersSetupStore.productList))
        setDataSource(businessParametersSetupStore.productLimit)

      }, [businessParametersSetupStore.productLimit])
      const handleDelete = key => {
        // const dataSource = [...this.state.dataSource];
        // setDataSource(dataSource.filter(item => item.key !== key))
        console.log(key)

      };

      const addKeyToDataSource = (arrayDataSource) => {
        let newDataSource = []
        for (let index = 0; index < arrayDataSource.length; index++) {

        }
      }
      const columns = [
        {
          // title: 'Specific Channel Limit',
          dataIndex: 'operation',
          render: (text, record) =>
            dataSource.length >= 1 ? (
              <Popconfirm title="Sure to delete?" onConfirm={() => businessParametersSetupStore.selectProductToDelete(record)} >
                <a>Delete</a>
              </Popconfirm>
            ) : null,
        },
        {
          title: 'Product_Code',
          dataIndex: 'product_code',
          width: '10%',
          editable: true,
        },
        {
          title: 'Product_Description',
          dataIndex: 'product_description',
        },
        {
          title: 'All-Channel Txn Limit',
          dataIndex: 'TxnLimit',
        },
        {
          title: 'All-Channel Daily Limit',
          dataIndex: 'daily_limit',
        },
        {
          title: 'Specific Channel Limit',
          dataIndex: 'Specific',
        },
      ];
      const mockDataSource = [
        {
          key: '0',
          ProductCode: 'NE',
          ProductDescription: 'Revolving Loan-Non TCG Nano',
          TxnLimit: '100,000.00',
          DailyLimit: '100,000.00'
        },
        {
          key: '1',
          ProductCode: 'NG',
          ProductDescription: 'Revolving Loan-Non TCG Micro',
          TxnLimit: '200,000.00',
          DailyLimit: '200,000.00'
        },
        {
          key: '2',
          ProductCode: 'NH',
          ProductDescription: 'Revolving Loan-TCG Nano',
          TxnLimit: '100,000.00',
          DailyLimit: '100,000.00'
        },
      ]
      return (
        <div>
          <Row>
            <Button
              // onClick={this.handleAdd}
              type="primary"
              style={{
                marginBottom: 16,
              }}
            >Add a row</Button>
          </Row>
          <Row>
            <Col flex={100}>
              <Table
                // components={components}
                rowClassName={() => 'editable-row'}
                bordered
                dataSource={dataSource}
                columns={columns}
                onRow={(record, rowIndex) => {
                  return {
                    onClick: event => { console.log('click' + record) }, // click row
                  };
                }}
              />
            </Col>

          </Row>
        </div>
      )
    }))
