import React, { useState } from 'react'
import { Input, Row, Col, Layout, Modal, Switch } from 'antd'
import styled from 'styled-components';
import SimpleSearch from '../simple-search'
const { Header, Footer, Sider, Content } = Layout;



export default function CustomerServicesMenu() {
  const [idCard, setIdCard] = useState('');
  const [isSearch, setIsSearch] = useState(false);
  const [visible, setVisble] = useState(false)
  const searchIdCardNumber = (value) => {
    console.log('eiei search:' + value)
    // setIdCard(value)
    setIsSearch(true)
  }


  const hideModal = () => {
    this.setState({
      visible: false,
    });
  };
  const onChange = (value) => {
    console.log("change:"+value)
    if(value === true){
      setVisble(true)
      // Modal.confirm({
      //   title: 'Confirm',
      //   content: 'Unlocking OTP!!Customer Id Card Number',
      //   okText: 'Confirm',
      //   cancelText: 'Cancel',
      // });
    }
  }
  return (
    <div>

      <Row>
        <Col span={12}>Account Unbinding</Col>
        <Col span={12}> OTP Unlocking</Col>
      </Row>
      <Row>
        <SimpleSearch search={searchIdCardNumber} />
      </Row>
      {(isSearch) ? (<Col span={12}>
        <Switch defaultChecked={false} onChange={checked=>onChange(checked)} />
      </Col>) : ('')}

      <Modal
          title="Modal"
          visible={visible}
          onOk={hideModal}
          onCancel={hideModal}
          okText="确认"
          cancelText="取消"
        >
          <p>Bla bla ...</p>
          <p>Bla bla ...</p>
          <p>Bla bla ...</p>
        </Modal>
    </div>
  )
}
