import React, { useState, useEffect } from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, InputNumber, Divider, Button, Modal, Drawer, Form, DatePicker, Space } from 'antd'
import { TcrbButton, TcrbPopconfirm } from '../../antd-styles/styles'

import { checkDefaultStatus } from '../../data-utility'
import SimpleInput from '../../simple-input'

const { Option } = Select;

const UserList = () => {
  const [visible, setVisible] = useState(false)
  const [mockDataUser, setMockDataUser] = useState([])
  const [form] = Form.useForm();

  useEffect(() => {
    setMockDataUser(testAddUser())
  }, [])
  const CollectionCreateForm = ({ visible, onCreate, onCancel }) => {
    return (
      <Modal
        visible={visible}
        title="Add new User"
        okText="Submit"
        cancelText="Cancel"
        onCancel={onCancel}
        width={900}
        onOk={() => {
          form
            .validateFields()
            .then(values => {
              form.resetFields();
              onCreate(values);
            })
            .catch(info => {
              console.log('Validate Failed:', info);
            });
        }}
      >
        <Form
          form={form}
          layout="vertical"
          name="form_in_modal"
          initialValues={{
            modifier: 'public',
          }}
        >

          <Row >
            <Col span={4} style={{ padding: 4 }}>
              <span>
                Employee ID
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="title"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <SimpleInput />
              </Form.Item>
            </Col>
            <Col span={4} style={{ paddingLeft: 16 }}>
              <span>
                Supervisor
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="supervisor"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <Select
                  style={{ width: '100%' }}
                  placeholder="Please select"
                  onChange={(value) => null}
                >
                  {/* {children} */}
                </Select>
              </Form.Item>
            </Col>
          </Row>
          <Row >
            <Col span={4} style={{ padding: 4 }}>
              <span>
                Name
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="name"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <SimpleInput />
              </Form.Item>
            </Col>
            <Col span={4} style={{ paddingLeft: 16 }}>
              <span>
                Surname
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="surname"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <SimpleInput />
              </Form.Item>
            </Col>
          </Row>
          <Row>
            <Col span={4} style={{ padding: 4 }}>
              <span>
                Username
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="Username"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <SimpleInput />
              </Form.Item>
            </Col>
            <Col span={4} style={{ paddingLeft: 16 }}>
              <span>
                Email
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="email"
                rules={[
                  {
                    type: 'email',
                    message: 'The input is not valid E-mail!',
                  },
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <SimpleInput />
              </Form.Item>
            </Col>
          </Row>
          <Row>
            <Col span={4} style={{ padding: 4 }}>
              <span>
                Join Date
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="jonDate"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <DatePicker style={{ width: '100%' }} />

              </Form.Item>
            </Col>
            <Col span={4} style={{ paddingLeft: 16 }}>
              <span>
                Last working date
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="lastWorkingDate"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <DatePicker style={{ width: '100%' }} />
              </Form.Item>
            </Col>
          </Row>
          <Row>
            <Col span={4} style={{ padding: 4 }}>
              <span>
                Status
              </span>
            </Col>
            <Col span={8}>
              <Form.Item
                name="status"
                rules={[
                  {
                    required: true,
                    message: 'Please input the title of collection!',
                  },
                ]}
              >
                <Select
                  style={{ width: '100' }}
