    // idCard= value
    // console.log({idCard})

    setIsSearch(true)
  }


  const hideModal = () => {
    this.setState({
      visible: false,
    });
  };
  const onChange = (value) => {
    console.log("change:" + value)

    if (value === true) {
      setVisble(true)
      setIsChecked(true)
      // Modal.confirm({
      //   title: 'Confirm',
      //   content: 'Unlocking OTP!!Customer Id Card Number',
      //   okText: 'Confirm',
      //   cancelText: 'Cancel',
      // });
    }else{
      setIsChecked(false)
    }
  }
  const unlockOTP = () => {

  }
  return (
    <div>

      <Row>
        <Col span={12}>Account Unbinding</Col>
        <Col span={12}> OTP Unlocking</Col>
      </Row>
      <Row>
        <SimpleSearch search={searchIdCardNumber} />
      </Row>
      {(isSearch) ? (<Col span={12}>
        <Switch defaultChecked={false} onChange={checked => onChange(checked)} />
        {isChecked ? (<StyledP>'OTP is Locked'</StyledP>):('OTP is ready for using')}
      </Col>) : ('')}
      <Modal
        title="Modal"
        visible={visible}
        onOk={hideModal}
        onCancel={()=>setVisble(false)}
        okText="Confirm"
        cancelText="Cancel"
      >
        <p>Unlocking OTP!!</p>
        <p>Customer ID Card Number {idCard}</p>
        <p>Mobile Number</p>
      </Modal>
    </div>
  )
}
