import React, { useState, useEffect } from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, Form, InputNumber, Divider, Button, Modal, Drawer } from 'antd'
import { TcrbButton, TcrbPopconfirm } from '../../antd-styles/styles'
import { inject, observer } from 'mobx-react'
import { orange, green, gold } from '@ant-design/colors';

import SimpleModal from '../../simple-modal'
import SimpleInput from '../../simple-input'

import { addKeyToDataSource } from '../../data-utility'
import UserAccessManagement from '../../../stores/user-access-management-store';

const { Option } = Select;
let departmentName = null
let sectionArray = null
const DepartmentList =
  inject('UserAccessManagementStore')
    (observer((props) => {
      const { UserAccessManagementStore, t } = props
      const [title, settitle] = useState("")
      const [textOk, settextOk] = useState("Submit")
      const [textEdit, setTextEdit] = useState("Add Section")
      const [textCancel, settextCancel] = useState("Cancel")
      const [modalString, setmodalString] = useState("initialState")
      const [modalType, setModalType] = useState('')
      const [visible, setvisible] = useState(false)
      const [visibleSection, setVisibleSection] = useState(false)
      const [mockDataDepartment, setMockDataDepartment] = useState([])
      const [addSection, setAddSection] = useState(false)
      const [editingKey, setEditingKey] = useState('')
      const [editingKeySection, setEditingKeySection] = useState('')
      const [form] = Form.useForm();


      const isEditing = record => record.key === editingKey


      const children = [];
      for (let i = 10; i < 36; i++) {
        children.push(<Option key={i.toString(36) + i} value={i}>Section_{i}</Option>);
      }

      const edit = record => {
        console.log(record)
        form.setFieldsValue({
          department: '',
          section: record.section,
          status: record.status,
          ...record,
        });
        setEditingKey(record.key);
      };

      const cancel = () => {
        setEditingKey('');
      };

      const EditableCell = ({
        editing,
        dataIndex,
        title,
        inputType,
        record,
        index,
        children,
        ...restProps
      }) => {
        // const inputNode = inputType === 'number' ? <InputNumber style={{ width: "100%" }} /> : <Input />;
        return (
          <td {...restProps}>
            {editing ? (
              <Form.Item
                name={dataIndex}
                style={{
                  margin: 0
                }}
                rules={[
                  {
                    required: true,
                    message: `Please Input ${title}!`,
                  },
                ]}
              >
                <Input />
              </Form.Item>
            ) : (
                children
              )
            }
          </td>
        );
      };



      const mockData = [
        {
          status: '1',
          department: 'Information Technology',
          request_status: '1',
          section: 3
          /*  section: [{
             section_name: 'section_1',
             section_id: '01',
             status: '1',
             key: 1
           },
           {
             section_name: 'section_2',
             section_id: '02',
             status: '1',
             key: 2
           },
           {
             section_name: 'section_3',
             section_id: '03',
             status: '1',
             key: 3
           }] */
        }
      ]

      const mockSection = [{
        section_name: 'section_1',
        team_name: 'team_1',
        section_id: '01',
        status: '1',
        key: 1
      },
      {
        section_name: 'section_2',
        team_name: 'team_2',
        section_id: '02',
        status: '1',
        key: 2
      },
      {
        section_name: 'section_3',
        team_name: 'team_3',
        section_id: '03',
        status: '1',
        key: 3
      }]



      useEffect(() => {
        //cal api
        addKeyToDataSource(mockData).then(result => {
          setMockDataDepartment(result)
        })
      }, [])

      /*  const mockDataDepartment = [
         {
           status: '1',
           department: 'Information Technology',
           section: []
         }
       ] */

      const save = async key => {
        const row = await form.validateFields();
        const newData = [...mockDataDepartment];
        const index = newData.findIndex(item => key === item.key);

        //waiting for call api
        if (index > -1) {
          const item = newData[index];
          newData.splice(index, 1, { ...item, ...row });
          setMockDataDepartment(newData);
          setEditingKey('');
        } else {
          newData.push(row);
          setMockDataDepartment(newData);
          setEditingKey('');
        }
        //config request api
        /*  let request = {
           currentData: item,
           newData: { ...item, ...row }
         } */
        // setEditingKey('');
      };

      const renderActionDepartment = (record) => {
        const editable = isEditing(record);
        if (record.status === '1') {
          return editable ? (
            <span>
              <TcrbPopconfirm title="Sure to Save?" onConfirm={() => save(record.key)}>
                <a style={{ marginRight: 8, }}>
                  Save
              </a>
              </TcrbPopconfirm>
              <TcrbPopconfirm title="Sure to cancel?" onConfirm={cancel}>
                <a style={{ color: '#3e3e3e' }}>Cancel</a>
              </TcrbPopconfirm>
            </span>
          ) : (
              <div style={{ textAlign: "center" }}>
                {/* <TcrbPopconfirm title="Sure to Edit?" onConfirm={() => edit(record)}> */}
                <a disabled={editingKey !== ''} onClick={() => viewDepartmentDetail(record)} style={{ marginRight: 8, color: '#FBA928' }}>
                  Edit
              </a>
                {/* </TcrbPopconfirm> */}
                <TcrbPopconfirm title="Sure to Deactivate?" >
