import React, { useState, useEffect } from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, Form, InputNumber, Divider, Button } from 'antd'
import { TcrbButton, TcrbPopconfirm } from '../antd-styles/styles'
import { inject, observer } from 'mobx-react'

import SimpleModal from '../simple-modal'
import SimpleInput from '../simple-input'

import { addKeyToDataSource } from '../data-utility'

const { Option } = Select;

const Department =
  inject('UserAccessManagementStore')
    (observer((props) => {
      const { UserAccessManagementStore, t } = props
      const [title, settitle] = useState("")
      const [textOk, settextOk] = useState("Ok")
      const [textCancel, settextCancel] = useState("Cancel")
      const [modalString, setmodalString] = useState("initialState")
      const [modalType, setModalType] = useState('')
      const [visible, setvisible] = useState(false)
      const [mockDataDepartment, setMockDataDepartment] = useState([])
      const [editSection, setEditSection] = useState(false)
      const [editingKey, setEditingKey] = useState('')
      const [form] = Form.useForm();


      const isEditing = record => record.key === editingKey;

      const children = [];
      for (let i = 10; i < 36; i++) {
        children.push(<Option key={i.toString(36) + i} value={i}>Section_{i}</Option>);
      }

      const edit = record => {
        form.setFieldsValue({
          department: '',
          ...record,
        });
        setEditingKey(record.key);
      };

      const cancel = () => {
        setEditingKey('');
      };

      const EditableCell = ({
        editing,
        dataIndex,
        title,
        inputType,
        record,
        index,
        children,
        ...restProps
      }) => {
        // const inputNode = inputType === 'number' ? <InputNumber style={{ width: "100%" }} /> : <Input />;
        return (
          <td {...restProps}>
            {editing ? (
              <Form.Item
                name={dataIndex}
                style={{
                  margin: 0
                }}
                rules={[
                  {
                    required: true,
                    message: `Please Input ${title}!`,
                  },
                ]}
              >
                <Input />
              </Form.Item>
            ) : (
                children
              )
            }
          </td>
        );
      };

      const mergedColumns = columnDepartment.map(col => {
        if (!col.editable) {
          return col;
        }

        return {
          ...col,
          onCell: record => ({
            record,
            inputType: 'text',
            dataIndex: col.dataIndex,
            title: col.title,
            editing: isEditing(record),
          }),
        };
      });

      const mockData = [
        {
          status: '1',
          department: 'Information Technology',
          request_status: '1',
          section: [{
            section_name: 'section_1',
            section_id: '01',
            status: '1',
            key: 1
          },
          {
            section_name: 'section_2',
            section_id: '02',
            status: '1',
            key: 2
          },
          {
            section_name: 'section_3',
            section_id: '03',
            status: '1',
            key: 3
          }]
        }
      ]

      const mockSection = [{
        section_name: 'section_1',
        section_id: '01',
        status: '1',
        key: 1
      },
      {
        section_name: 'section_2',
        section_id: '02',
        status: '1',
        key: 2
      },
      {
        section_name: 'section_3',
        section_id: '03',
        status: '1',
        key: 3
      }]

      useEffect(() => {
        //cal api
        addKeyToDataSource(mockData).then(result => {
          setMockDataDepartment(result)
        })
      }, [])

      /*  const mockDataDepartment = [
         {
           status: '1',
           department: 'Information Technology',
           section: []
         }
       ] */


      const renderActionDepartment = (record) => {
        if (record.status === '1') {
          return <span>
            <TcrbPopconfirm title="Sure to Edit?" onConfirm={() => edit(record)}>
              <a style={{ marginRight: 8, }}>
                Edit
              </a>
            </TcrbPopconfirm>
            <TcrbPopconfirm title="Sure to Deactivate?" >
              <a style={{ color: '#3e3e3e' }}>Deactivate</a>
            </TcrbPopconfirm>
          </span>
        } else {
          return null
        }
      }

      const renderSection = (record) => {
        return <div>
          <a onClick={() => selectSection(record)}>{record.section.length} Section</a>
        </div>
      }

      const columnDepartment = [
        {
          title: '',
          dataIndex: 'status',
          width: '5%',
          // render: (text, record) => checkStatus(record)
        },
        {
          title: 'Department',
          dataIndex: 'department',
          editable: true,
          // render: (text, record) => (record.partner_code + "/" + record.partner_abbreviation)
        },
        {
          title: 'Section / Team',
          dataIndex: 'section',
          render: (text, record) => renderSection(record)
        },
        {
          title: 'Action',
          dataIndex: 'operation',
          render: (text, record) => renderActionDepartment(record)
        }
      ]

      const columnSection = [
        {
          title: '',
          dataIndex: 'status',
          width: '5%',
          // render: (text, record) => checkStatus(record)
        },
        {
          title: 'Section / Team',
          dataIndex: 'section_name',
          // render: (text, record) => (record.partner_code + "/" + record.partner_abbreviation)
        },
        {
          title: 'Action',
          dataIndex: 'operation',
          // render: (text, record) => renderActionDepartment(record)
        }
      ]

      const AddDepartment = () => {
        return (
          <div>
            <Row>
              <Col span={10} style={{ padding: 4 }}>
                <p>
                  Department Name
            </p>
              </Col>
              <Col span={14}>
                <SimpleInput onChange={(value) => console.log(value)} />
              </Col>
            </Row>
          </div>
        )
      }

      const handleChange = (value) => {
        console.log(value)
      }

      const SelectDepartment = (props) => {
        return (
          <Row>
            <Col span={10} style={{ padding: 4 }}>
              <p>Section / Team</p>
            </Col>
            <Col span={14}>
              <Select
                mode="multiple"
                style={{ width: '100%' }}
                placeholder="Please select"
                onChange={(value) => handleChange(value)}
                defaultValue={[10, 11, 14]}
              >
                {children}
              </Select>
            </Col>
          </Row>

        )
      }

