import React, { useState, useEffect, useMemo, memo } from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, InputNumber, Divider, Button, Modal, Drawer, Form, DatePicker, Space } from 'antd'
import { TcrbButton, TcrbPopconfirm } from '../../antd-styles/styles'
import { inject, observer } from 'mobx-react'

import moment from 'moment';

import { checkDefaultStatus, addKeyToDataSource } from '../../data-utility'
import SimpleInput from '../../simple-input'

const { Option } = Select;

const UserList = inject('userAccessManagementStore')
  (observer((props) => {
    const [visible, setVisible] = useState(false)
    const [userList, setUserList] = useState([])
    const [testSupervisor, setTestSupervisor] = useState([])
    const [form] = Form.useForm();
    const { userAccessManagementStore } = props
    const dateFormat = 'YYYY-MM-DD'

    useEffect(() => {
      userAccessManagementStore.getDataUser()
    }, [])

    useEffect(() => {
      if (userAccessManagementStore.userList.length >= 0) {
        addKeyToDataSource(userAccessManagementStore.userList).then(result => {
          setUserList(result)
        })
      }
    }, [userAccessManagementStore.userList])

    const testAddSupervisor = (id) => {
      setTestSupervisor([
        { id: 0, name: sup_1 },
        { id: 1, name: sup_2 },
        { id: 2, name: sup_3 },
        { id: 3, name: sup_4 },
        { id: 4, name: sup_5 },

      ])
    }

    // const MemoModalCreateForm = useMemo((visible, onCreate, onCancel, testSupervisor) => CollectionCreateForm)
    const CollectionCreateForm = ({ visible, onCreate, onCancel, testSupervisor }) => {
      return (
        <Modal
          visible={visible}
          title="Add new User"
          okText="Submit"
          cancelText="Cancel"
          onCancel={onCancel}
          width={900}
          onOk={() => {
            form
              .validateFields()
              .then(values => {
                form.resetFields();
                onCreate(values);
              })
              .catch(info => {
                console.log('Validate Failed:', info);
              });
          }}
        >
          <Form
            form={form}
            layout="vertical"
            name="form_in_modal"
            initialValues={{
              modifier: 'public',
            }}
          >

            <Row >
              <Col span={4} style={{ padding: 4 }}>
                <span>
                  Employee ID
              </span>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="employee_code"
                  rules={[
                    {
                      required: true,
                      message: 'Please input Employee Id!',
                    },
                  ]}
                >
                  <SimpleInput />
                </Form.Item>
              </Col>
              <Col span={4} style={{ paddingLeft: 16 }}>
                <span>
                  Supervisor
              </span>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="supervisor_id"
                  rules={[
                    {
                      required: true,
                      message: 'Please input Supervisor!',
                    },
                  ]}
                >
                  <Select
                    style={{ width: '100%' }}
                    placeholder="Please select"
                    onChange={(value) => null}
                  >
                    {/* {testSupervisor.map((item, index) => <Option key={index} value={item.id}>{item.name}</Option>)} */}
                  </Select>
                </Form.Item>
              </Col>
            </Row>
            <Row >
              <Col span={4} style={{ padding: 4 }}>
                <span>
                  Name
              </span>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="name"
                  rules={[
                    {
                      required: true,
                      message: 'Please input Name!',
                    },
                  ]}
                >
                  <SimpleInput />
                </Form.Item>
              </Col>
              <Col span={4} style={{ paddingLeft: 16 }}>
                <span>
                  Surname
              </span>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="surname"
                  rules={[
                    {
                      required: true,
                      message: 'Please input Surname!',
                    },
                  ]}
                >
                  <SimpleInput />
                </Form.Item>
              </Col>
            </Row>
            <Row>
              <Col span={4} style={{ padding: 4 }}>
                <span>
                  Username
              </span>
              </Col>
              <Col span={8}>
                <Form.Item
                  name="user_name"
                  rules={[
                    {
                      required: true,
