import { toJS } from 'mobx'
export default (observer(function PendingApprovals() {
  const [dataSource, setDataSource] = useState([])
  const { businessParametersSetupStore } = useStores()

  useEffect(() => {
    let pendingApprovalsRawData = toJS(businessParametersSetupStore.pendingApprovals)
      setDataSource(pendingApprovalsRawData =mockDataSource)
      console.log(pendingApprovalsRawData)
  }, [businessParametersSetupStore.pendingApprovals]);

  const handleDelete = key => {
    // const dataSource = [...this.state.dataSource];
    console.log(key)
    // setDataSource(dataSource.filter(item => item.key !== key))

  };
  const columns = [

    {
      title: 'Ticket#',
      dataIndex: 'ticket',
      width: '10%',
      // editable: true,
    },
    {
      title: 'Request Type',
      dataIndex: 'requestType',
    },
    {
      title: 'Request Description',
      dataIndex: 'requestDescription',
    },
    {
      title: 'Requester ID',
      dataIndex: 'requesterId',
    },
    {
      title: 'Request Date',
      dataIndex: 'requestDate',
    },
    {
      title: 'Action',
      dataIndex: 'action',
    //   render: (text, record) =>
    //     dataSource.length >= 1 ? (
    //       <Popconfirm title="Sure to delete?" onConfirm={() => handleDelete(record.key)}>
    //         <a>Delete</a>
