import React from 'react'
import { Table, Row, Col, Menu, Card, Input, Select, Form, InputNumber, Divider } from 'antd'
import { TcrbButton, TcrbPopconfirm } from '../antd-styles/styles'

const { Option } = Select;

const Department = () => {
  const [title, settitle] = useState("Action details")
  const [textOk, settextOk] = useState("Ok")
  const [textCancel, settextCancel] = useState("Cancel")
  const [modalString, setmodalString] = useState("initialState")
  const [visible, setvisible] = useState(false)

  const children = [];
  for (let i = 10; i < 36; i++) {
    children.push(<Option key={i.toString(36) + i}>{i.toString(36) + i}</Option>);
  }

  const columnDepartment = [
    {
      title: '',
      dataIndex: 'status',
      width: '5%',
      // render: (text, record) => checkStatus(record)
    },
    {
      title: 'Department',
      dataIndex: 'partner_code',
      // render: (text, record) => (record.partner_code + "/" + record.partner_abbreviation)
    },
    {
      title: 'Section / Team',
      dataIndex: 'transaction_limit',
      editable: true,
      // render: (text, record) => addCommaInData(text, true)
    },
    {
      title: 'Action',
      dataIndex: 'daily_limit',
      editable: true,
      // render: (text, record) => addCommaInData(text, true)
    }
  ]

  const handleChange = (value) => {
    console.log(value)
  }
  const SelecteDepartment = () => {
    return <Select
      mode="multiple"
      style={{ width: '100%' }}
      placeholder="Please select"
      defaultValue={['a10', 'c12']}
      onChange={handleChange(value)}
    >
      {children}
    </Select>
  }
  const openModalAddDepartment = () => {
    settitle('Add Department')
    setmodalString(<SelecteDepartment />)
    setvisible(true)
  }

  const confirmDepartmentSelected = () => {

  }
  return (
    <div>
      <Row gutter={[4, 24]}>
        <Col span={2}>
          <TcrbButton className="primary" onClick={() => openModalAddDepartment()} shape="round">Add</TcrbButton>
        </Col>
      </Row>
      <Table
        bordered
        dataSource={[]}
        columns={columnDepartment}
        size="small"
      />
      <SimpleModal
        title={title}
        // type={modalType}
        onOk={() => setvisible()}
        onCancel={() => setvisible(false)}
        textCancel={textCancel}
        textOk={textOk}
        width={800}
        modalString={modalString}
        visible={visible}
      />

    </div>
  )
}

export default Department
